import React, { useState, useEffect } from "react";
import "./App.css"; // Optional: For styling

function App() {
  const [jwt, setJwt] = useState("");
  const [header, setHeader] = useState(null);
  const [payload, setPayload] = useState(null);
  const [signature, setSignature] = useState("");
  const [error, setError] = useState("");

  const decodeBase64Url = (str) => {
    try {
      const base64 = str.replace(/-/g, "+").replace(/_/g, "/");
      const decoded = atob(base64);
      return JSON.parse(decoded);
    } catch (err) {
      throw new Error("Decoding failed");
    }
  };

  const decodeJWT = () => {
    setError("");
    setHeader(null);
    setPayload(null);
    setSignature("");

    if (!jwt.includes(".")) {
      setError("Invalid JWT format. Missing '.' separators.");
      return;
    }

    const parts = jwt.split(".");
    if (parts.length !== 3) {
      setError("JWT should have 3 parts (header.payload.signature)");
      return;
    }

    try {
      const decodedHeader = decodeBase64Url(parts[0]);
      const decodedPayload = decodeBase64Url(parts[1]);
      setHeader(decodedHeader);
      setPayload(decodedPayload);
      setSignature(parts[2]);
    } catch (err) {
      setError("Error decoding JWT: " + err.message);
    }
  };

  const handleClear = () => {
    setJwt("");
    setHeader(null);
    setPayload(null);
    setSignature("");
    setError("");
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    alert("Copied to clipboard!");
  };

  // Bonus Feature: Auto-decode on input change
  useEffect(() => {
    if (jwt.trim().length > 0) {
      decodeJWT();
    }
  }, [jwt]);

  return (
    <div className="App" style={styles.container}>
      <h2>🛡️ JWT Decoder App</h2>

      <textarea
        placeholder="Paste your JWT here..."
        value={jwt}
        onChange={(e) => setJwt(e.target.value)}
        style={styles.textarea}
      />

      <div style={styles.buttonRow}>
        <button onClick={decodeJWT} style={styles.button}>
          Decode
        </button>
        <button onClick={handleClear} style={styles.button}>
          Clear
        </button>
        <button onClick={() => copyToClipboard(jwt)} style={styles.button}>
          Copy JWT
        </button>
      </div>

      {error && <p style={styles.error}>{error}</p>}

      {header && (
        <div style={styles.outputBox}>
          <h3>🔐 Header</h3>
          <pre>{JSON.stringify(header, null, 2)}</pre>
          <button onClick={() => copyToClipboard(JSON.stringify(header))}>
            Copy Header
          </button>
        </div>
      )}

      {payload && (
        <div style={styles.outputBox}>
          <h3>📦 Payload</h3>
          <pre>{JSON.stringify(payload, null, 2)}</pre>
          <button onClick={() => copyToClipboard(JSON.stringify(payload))}>
            Copy Payload
          </button>
        </div>
      )}

      {signature && (
        <div style={styles.outputBox}>
          <h3>✍️ Signature</h3>
          <p>{signature}</p>
          <button onClick={() => copyToClipboard(signature)}>
            Copy Signature
          </button>
        </div>
      )}
    </div>
  );
}

const styles = {
  container: {
    maxWidth: "600px",
    margin: "0 auto",
    padding: "1rem",
    fontFamily: "Arial",
  },
  textarea: {
    width: "100%",
    height: "100px",
    padding: "10px",
    fontSize: "1rem",
  },
  buttonRow: {
    marginTop: "1rem",
    display: "flex",
    gap: "10px",
    flexWrap: "wrap",
  },
  button: {
    padding: "0.5rem 1rem",
    fontSize: "1rem",
    cursor: "pointer",
  },
  error: {
    color: "red",
    marginTop: "10px",
  },
  outputBox: {
    marginTop: "20px",
    padding: "10px",
    backgroundColor: "#f5f5f5",
    borderRadius: "8px",
  },
};

export default App;
